dofile("C:/Users/jr/Desktop/LD32/LD32.nes.lua")local ypos = 0local xpos = 0function p_reset(x,y)	xpos = x	ypos = yendfunction print_line(text)	gui.text(xpos, ypos, text)	ypos = ypos + 8endfunction p8(varname,add)	if add==nil then		add = 0	end	if(type(varname)=="number") then		gui.text(xpos, ypos, string.format("%04X:%02X",varname,memory.readbyte(varname+add)))	else		gui.text(xpos, ypos, string.format("%s:%02X",varname,memory.readbyte(var[varname]+add)))	end	ypos = ypos + 8endfunction p16(varname,add)	if add==nil then		add = 0	end	if(type(varname)=="number") then		gui.text(xpos, ypos, string.format("%04X:%02X%02X",varname,memory.readbyte(varname+1),memory.readbyte(varname+add)))	else		gui.text(xpos, ypos, string.format("%s:%02X%02X",varname,memory.readbyte(var[varname]+1),memory.readbyte(var[varname]+add)))	end	ypos = ypos + 8endfunction get_flags(v)	str = ""	if(AND(v,0x80)==0x80) then		str = str .. "V"	else		str = str .. "v"	end	if(AND(v,0x40)==0x40) then		str = str .. "H"	else		str = str .. "h"	end	if(AND(v,0x20)==0x20) then		str = str .. "C"	else		str = str .. "c"	end	if(AND(v,0x10)==0x10) then		str = str .. "G"	else		str = str .. "g"	end	if(AND(v,0x08)==0x08) then		str = str .. "R"	else		str = str .. "r"	end	if(AND(v,0x04)==0x04) then		str = str .. "L"	else		str = str .. "l"	end	if(AND(v,0x02)==0x02) then		str = str .. "1"	else		str = str .. "0"	end	if(AND(v,0x01)==0x01) then		str = str .. "1"	else		str = str .. "0"	end	return strendfunction get_flags2(v)	str = ""	if(AND(v,0x80)==0x80) then		str = str .. "H"	else		str = str .. "h"	end	if(AND(v,0x40)==0x40) then		str = str .. "W"	else		str = str .. "w"	end	if(AND(v,0x20)==0x20) then		str = str .. "I"	else		str = str .. "i"	end	if(AND(v,0x10)==0x10) then		str = str .. "g"	else		str = str .. "G"	end	if(AND(v,0x08)==0x08) then		str = str .. "c"	else		str = str .. "C"	end	if(AND(v,0x04)==0x04) then		str = str .. "P"	else		str = str .. "p"	end	return str..".."end